import React from "react";
import { saveNews } from "../Utils/News";
import Card from 'react-bootstrap/Card';

export const NewsCard = ({ article }) => {
  const saveForLater = () => {
    //article
    saveNews({ ...article }).then(() => {
      //show success alert bootstrap, hide with settimeout after 3 seconds, useState
    });
  };
  return (
    <div>
	  <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={article.urlToImage} />
      <Card.Body>
        <Card.Title>{article.title}</Card.Title>
        <Card.Text>
          {article.content}
        </Card.Text>
      </Card.Body>
      <Card.Body>
        <Card.Text>{article.author}</Card.Text>
        <Card.Link href="#" onClick={saveForLater}>read news later</Card.Link>
      </Card.Body>
    </Card>
    </div>
  );
};
