import React from "react";
import { saveNews } from "../Utils/News";
import Card from "react-bootstrap/Card";

export const NewsCard = ({ article }) => {
  const saveForLater = () => {
    //article
    saveNews({ ...article }).then(() => {
      //show success alert bootstrap, hide with settimeout after 3 seconds, useState
    });
  };
  return (
    <div style={{ paddingRights: "20px", flex: "1" }}>
      <Card
        style={{
          width: "18rem",
          border: "2px solid black",
          borderRadius: "10px",
		  height: '600px',
		  width: '300px',
        }}
      >
        <Card.Img variant="top" src={article.urlToImage} className='img-responsive' />
        <Card.Body>
          <Card.Title>{article.title}</Card.Title>
          <Card.Text>{article.content}</Card.Text>
        </Card.Body>
        <Card.Body>
          <Card.Text>{article.author}</Card.Text>
          <Card.Link href="#" onClick={saveForLater}>
            read news later
          </Card.Link>
        </Card.Body>
      </Card>
    </div>
  );
};
